pragma solidity ^0.4.24;
import "./ERC20.sol";

contract ZapBridge{
    function getContract(string contractName) public view returns (address); //coordinator
    function calcZapForDots(address, bytes32, uint256) external view returns (uint256); //bondage
    function delegateBond(address holderAddress, address oracleAddress, bytes32 endpoint, uint256 numDots) external returns (uint256 boundZap); //bondage
    function query(address, string, bytes32, bytes32[]) external returns (uint256); //dispatch
    function respondIntArray(uint256, string) external returns (bool); //dispatch
}


contract priceBet {

    address public player1;
    address public owner;
    address public provider;
    ZapBridge public coordinator;
    address public player2;
    string public coin;
    uint256 public expire;
    uint256 public price;
    bool public higher;
    bool public taken;
    bool public inplay;
    uint256 public betAmount;
    bytes32 public endpoint;
    uint256 public id;
    ERC20 token;

    event Settlement(
        address indexed _winner,
        uint256 indexed _settlementAmount,
        uint256 indexed _finalPrice
    );

    constructor(address _coordinator) {
        owner = msg.sender;
        taken = false;
        inplay=false;
        coordinator = ZapBridge(_coordinator);
        address zapTokenAddress = coordinator.getContract("ZAP_TOKEN");
        token = ERC20(zapTokenAddress);
    }

    function setProvider(address _provider, bytes32 _endpoint){
        require(msg.sender == owner);
        endpoint = _endpoint;
        provider = _provider;
    }

    ///Taker has to have enough zap approved to bond 1 dot for settlement
    function takeBet() payable returns (bool){
        require(!taken);
        uint256 zapBalance = token.balanceOf(msg.sender);
        address bondage = coordinator.getContract("BONDAGE");
        uint256 zapRequied = ZapBridge(bondage).calcZapForDots(provider,endpoint,1);
        require(zapBalance>zapRequied);
        require(msg.value == betAmount);
        player2 = msg.sender;
        taken = true;
        return taken;
    }

    function makeBet(string _coin, uint256 _price, bool _higher,  uint256 _expire) payable returns (bool success)
    {
        require(!inplay && msg.value>0);
        inplay=true;
        betAmount= address(this).balance;
        player1 = msg.sender;
        coin = _coin;
        price = _price;
        expire = _expire;
        higher = _higher;
        success = true;
    }


    function getBetInfo() view returns (bool, address , string , uint256 ,
        bool , uint256,  bool , address ,uint256){
        return (inplay,player1,coin,price,higher,betAmount,taken,player2,expire);
    }

    //choice to query offchain or onchain provider
    function queryProvider() returns (uint256) {
        require(msg.sender == player2 || msg.sender == player1);
        address dispatchAddress = coordinator.getContract("DISPATCH");
        bytes32[] memory params = new bytes32[](1);
        params[0] = bytes32(expire);
        id = ZapBridge(dispatchAddress).query(provider,coin,endpoint,params);
        return id;
    }

    function reset(){
        delete player1;
        delete player2;
        delete coin;
        delete price;
        delete expire;
        delete higher;
        taken=false;
        inplay=false;
    }

    function callback(uint256 _id, int[] _response) returns (address winner){
        require(_id==id);
        require(address(this).balance>0);
        uint256 _price = uint256(_response[0]);
        if(higher==(_price>price)){
            player1.transfer(address(this).balance);
            winner = player1;
        }
        else{
            player2.transfer(address(this).balance);
            winner = player2;
        }
        emit Settlement(winner,betAmount,_price);
        reset();
        return winner;
    }

}
